/*      Slide show                  */
document.getElementsByClassName("sld-btn-chld")[0].style.borderRadius = "0 10px 10px 0";
document.getElementsByClassName("sld-btn-chld")[1].style.borderRadius = "10px 0 0 10px";
var bcMg = [
    "https://news.ufl.edu/media/newsufledu/images/2018/11/Food-Traceability.jpg",
    "https://www.theallineed.com/wp-content/uploads/2018/06/Juices.jpg",
    "http://thespaces.com/wp-content/uploads/2017/01/London-restaurants-Aquavit.jpg"
];
function sld() {
    for (const i of document.getElementsByClassName("sld-shw")) {
        i.style.height = window.innerHeight + "px";
    };
    for (const i of document.getElementsByClassName("sld-bcmg")) {
        i.style.height = window.innerHeight + "px";
    };
    for (const i of document.getElementsByClassName("sld-chld")) {
        i.style.width = ((window.innerWidth) - (document.getElementsByClassName("sld-btn")[0].clientWidth + document.getElementsByClassName("sld-btn")[1].clientWidth)) + "px";
    };
    for (const i of document.getElementsByClassName("sld-btn")) {
        for (const ic of document.getElementsByClassName("sld-btn-chld")) {
            ic.style.marginTop = ((i.clientHeight / 2) - (ic.clientHeight / 2)) + "px";
            ic.style.marginBottom = ((i.clientHeight / 2) - (ic.clientHeight / 2)) + "px";
            ic.onmouseover = function () {
                ic.classList.add("hover");
            };
            ic.onmouseout = function () {
                ic.classList.remove("hover");
            };
        };
    };
    for (const i of document.getElementsByClassName("sld-txt")) {
        i.style.marginTop = ((document.getElementsByClassName("sld-shw")[0].clientHeight / 2) - i.clientHeight / 2) + "px";
    };
};
setInterval(sld, 0);
sld();
for (const i of document.getElementsByClassName("sld-chld")) {
    i.style.display = "none";
};
document.getElementsByClassName("sld-chld")[0].style.display = "block";
var sldIndex;
var status = 1;
var i = 0;
function sldShw(sldIndx) {
    i = i + sldIndx;
    if (i >= document.getElementsByClassName("sld-chld").length) {
        i = 0;
    } else if (i <= -1) {
        i = document.getElementsByClassName("sld-chld").length - 1;
    };
    for (const ic of document.getElementsByClassName("sld-chld")) {
        ic.style.display = "none";
        document.getElementsByClassName("sld-chld")[i].style.display = "block";
    };
    status = 0;
    bcMgF();
};
function sldAuto() {
    i = i + 1;
    if (status == 1) {
        if (i >= document.getElementsByClassName("sld-chld").length) {
            i = 0;
        } else if (i <= -1) {
            i = document.getElementsByClassName("sld-chld").length - 1;
        };
        for (const ic of document.getElementsByClassName("sld-chld")) {
            ic.style.display = "none";
            document.getElementsByClassName("sld-chld")[i].style.display = "block";
        };
        bcMgF();
    } else {
        clearInterval();
        setTimeout(function () {
            status = 1;
        }, 5000)
    };
};
setInterval(sldAuto, 3000);
sldAuto();
function bcMgF() {
    for (let ic = 0; ic < document.getElementsByClassName("sld-chld").length; ic++) {
        if (document.getElementsByClassName("sld-chld")[ic].style.display == "block") {
            document.getElementsByClassName("sld-bcmg")[0].style.backgroundImage = `url(${bcMg[ic]})`;
        };
    };
};
/*      Header                      */
document.getElementsByClassName("lgo-hdr")[0].onmouseover = function lgoHdrF() {
    document.getElementsByClassName("lgo-hdr")[0].lastElementChild.style.color = "rgba(69, 48, 40, 1)";
};
document.getElementsByClassName("lgo-hdr")[0].onmouseout = function lgoHdrF() {
    document.getElementsByClassName("lgo-hdr")[0].lastElementChild.style.color = "rgba(255, 255, 255, 1)";
};
function hdrScrollF() {
    if (window.scrollY > document.getElementsByClassName("sld-shw")[0].offsetHeight) {
        document.getElementsByClassName("hdr")[0].style.position = "fixed";
        document.getElementsByClassName("hdr")[0].style.top = "0";
    } else {
        document.getElementsByClassName("hdr")[0].style.position = "static";
    }
};
setInterval(hdrScrollF, 0);
hdrScrollF();